def generate_preview(image, mode, threshold, invert, flip_y, line_step, brightness=1.0, contrast=1.0, outline_levels=2, smoothing=2.0):
    """Generate a high-fidelity preview of the DXF output - black on white like CAD software."""
    img = image.copy().convert('L')
    w, h = img.size
    
    # Apply brightness and contrast adjustments for ALL modes
    if brightness != 1.0:
        enhancer = ImageEnhance.Brightness(img)
        img = enhancer.enhance(brightness)
    if contrast != 1.0:
        enhancer = ImageEnhance.Contrast(img)
        img = enhancer.enhance(contrast)
    
    # Create preview image - WHITE background, BLACK lines (like CAD software)
    preview = Image.new('RGB', (w, h), '#ffffff')
    draw = ImageDraw.Draw(preview)
    
    # Apply inversion to source if needed
    if invert:
        img = ImageOps.invert(img)
    
    # Apply flip Y to match DXF output
    # When Flip Y is checked, flip the preview to show how it will appear in CAD
    if flip_y:
        img = ImageOps.flip(img)
    
    px = img.load()
    
    if mode == "floyd_steinberg":
        # Apply actual Floyd-Steinberg dithering
        pixels = list(img.getdata())
        data = [[float(pixels[y * w + x]) for x in range(w)] for y in range(h)]
        
        for y in range(h):
            for x in range(w):
                old_pixel = data[y][x]
                new_pixel = 255 if old_pixel > 127 else 0
                data[y][x] = new_pixel
                error = old_pixel - new_pixel
                
                if x + 1 < w:
                    data[y][x + 1] += error * 7 / 16
                if y + 1 < h:
                    if x > 0:
                        data[y + 1][x - 1] += error * 3 / 16
                    data[y + 1][x] += error * 5 / 16
                    if x + 1 < w:
                        data[y + 1][x + 1] += error * 1 / 16
        
        # Draw small BLACK dots at actual line_step intervals
        dot_radius = max(0, min(1, line_step // 4))
        for y in range(0, h, line_step):
            for x in range(0, w, line_step):
                if data[y][x] < 128:
                    if dot_radius == 0:
                        draw.point((x, y), fill='#000000')
                    else:
                        draw.ellipse(
                            [x - dot_radius, y - dot_radius, x + dot_radius, y + dot_radius],
                            fill='#000000'
                        )
    
    elif mode == "outline":
        # Apply smoothing based on parameter
        if smoothing > 0:
            from PIL import ImageFilter
            # Apply Gaussian blur proportional to smoothing value
            blur_radius = smoothing / 2.0
            img = img.filter(ImageFilter.GaussianBlur(radius=blur_radius))
        
        # Multi-level contour detection based on outline_levels
        # Create multiple threshold levels across the grayscale range
        edges_img = Image.new('L', (w, h), 255)
        edges_px = edges_img.load()
        img_px = img.load()
        
        # Generate contour lines at different gray levels
        for level in range(outline_levels):
            # Distribute thresholds across the grayscale range
            level_threshold = int(255 * (level + 1) / (outline_levels + 1))
            
            # Detect edges at this threshold level
            for y in range(1, h - 1):
                for x in range(1, w - 1):
                    # Check if this pixel crosses the threshold compared to neighbors
                    center = img_px[x, y]
                    if ((center < level_threshold and img_px[x+1, y] >= level_threshold) or
                        (center >= level_threshold and img_px[x+1, y] < level_threshold) or
                        (center < level_threshold and img_px[x, y+1] >= level_threshold) or
                        (center >= level_threshold and img_px[x, y+1] < level_threshold)):
                        edges_px[x, y] = 0
        
        # Draw the contour lines
        for y in range(h):
            for x in range(w):
                if edges_px[x, y] == 0:
                    draw.point((x, y), fill='#000000')
    
    else:  # threshold mode
        # Draw thin BLACK horizontal scan lines exactly as DXF will have them
        for y in range(0, h, line_step):
            x = 0
            while x < w:
                # Find start of black region
                while x < w and px[x, y] >= threshold:
                    x += 1
                if x >= w:
                    break
                x1 = x
                # Find end of black region
                while x < w and px[x, y] < threshold:
                    x += 1
                x2 = x
                # Draw thin BLACK line segment (width=1)
                if x2 > x1:
                    draw.line([(x1, y), (x2, y)], fill='#000000', width=1)
    
    return preview


